import { Component } from '@angular/core';
import { WeatherService } from '../services/weather.service';

interface CityWeather {
  name: string;
  temperature: number;
  humidity: number;
}

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
  cities: CityWeather[] = [];
  cityInput: string = '';
  private apiUrl = 'http://localhost:8080/weather';

  constructor(private weatherService: WeatherService) {}

  addCity(): void {
    if (this.cityInput) {
      const formattedCityName = this.formatCityName(this.cityInput);

      // Check if the city already exists
      if (this.isCityDuplicate(formattedCityName)) {
        alert('City already exists!');
      } else {
        this.fetchWeatherData(formattedCityName);
      }

      this.cityInput = '';
    }
  }

  // add number validation for city or special character

  private formatCityName(cityName: string): string {
    return cityName.charAt(0).toUpperCase() + cityName.slice(1).toLowerCase();
  }

  private isCityDuplicate(cityName: string): boolean {
    return this.cities.some(city => city.name.toLowerCase() === cityName.toLowerCase());
  }

  private fetchWeatherData(cityName: string): void {
    this.weatherService.getWeather(cityName).subscribe(
      (data: any) => {
        if (data && data.error) {
          if(data.error === "City not found") {
            alert(`City "${cityName}" does not exist.`);
          } else {
            alert(data.error); 
          }
        } else {
        const cityWeather: CityWeather = {
          name: cityName,
          temperature: data.main.temp,
          humidity: data.main.humidity
        };
        this.cities.push(cityWeather);
      }
      },
      (error) => {
        if (error.status === 404) {
          alert(`City "${cityName}" does not exist.`);
        } else {}
        console.log(error);
        alert ('Error fetching weather data. Please try again later.');
      }

    );
    }




  removeCity(city: CityWeather): void {
    const index = this.cities.indexOf(city);
    if (index !== -1) {
      this.cities.splice(index, 1);
    }
  }

  getBackgroundImageUrl(temperature: number): string {
    if (temperature > 30) {
      return '../../assets/sunny.webp';
    } else if (temperature < 20) {
      return '../assets/Cold.webp';
    } else {
      return '../../assets/sunny.webp';
    }
  }
}
<div class="container">
    <h1 class="title">Weather App</h1>
    <div class="input-container">
      <input type="text" [(ngModel)]="cityInput" placeholder="Enter city name">
      <button (click)="addCity()">Add City</button>
    </div>

    <div class="cards-container">
      <div class="card" *ngFor="let city of cities" [style.background-image]="'url(' + getBackgroundImageUrl(city.temperature) + ')'">
        <div class="remove-cross" (click)="removeCity(city)">&#10005;</div>
        <div class="city-details">
          <div class="location">{{ city.name }}</div>
          <div class="details-bottom">
            <div class="temperature">Temperature: {{ city.temperature | number: '1.0-0'}}°C</div>
            <div class="humidity">Humidity: {{ city.humidity }}%</div>
          </div>
        </div>
      </div>
    </div>
  </div>
