import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {

  weather: any;
  city: string = '';
  //constructor(private weatherService : WeatherService){ } 
  constructor(private http: HttpClient){}

  private apiUrl = 'http://localhost:8080/weather';

  getWeather(city : string) : void {
    console.log("get weather");
    console.log(city);
    const url = `${this.apiUrl}/${city}`;
    console.log(url);
    let response = this.http.get(url);
    console.log(response);
    response.subscribe(
      (data) => { 
      this.weather=data;
      console.log(this.weather);
      console.log("max temp ", this.weather.main.temp_max);
      console.log("max temp ", this.weather.main.temp_min);
    },
    (error) => {
      console.log(error);
    }
    );
    console.log("abcd");
  }

  onSubmit() : void {
    this.getWeather(this.city);
  }
}


<!-- // using ng form -->

<div>
    <h2>Weather Information</h2>
    <form (ngSubmit)="onSubmit()" #weatherForm="ngForm">
        <input type="text" name="city" [(ngModel)]="city" placeholder="Enter city name">
        <button type="submit">Get Weather</button>
    </form>

    <div *ngIf="weather">
        <h3>Temperature</h3>
        <p>Max temp {{ weather.main.temp_max }}°C</p>
        <p>Min temp {{ weather.main.temp_min }}°C</p>
    </div>
</div>
